struct KbState {
    int ctrl;
    int shift;
    int alt;
    int caps;
};
struct KbState kbstate = {0};
static unsigned int idx = 0;
unsigned int get_keymap_idx(void) {
	idx = 0;
	idx += (kbstate.ctrl << 3);
	idx += (kbstate.shift << 2);
	idx += (kbstate.alt << 1);
	idx += (kbstate.caps << 0);
	return idx;
}

const char* key_press(int c) {
    if((c == KEY_LEFTCTRL) || (c == KEY_RIGHTCTRL)) kbstate.ctrl = 1;
    if((c == KEY_LEFTSHIFT) || (c == KEY_RIGHTSHIFT)) kbstate.shift = 1;
    if((c == KEY_LEFTALT) || (c == KEY_RIGHTALT)) kbstate.alt = 1;
    if(c == KEY_CAPSLOCK) kbstate.caps = !kbstate.caps;
    return keymap[c][get_keymap_idx()];
}

void key_release(int c) {
    if((c == KEY_LEFTCTRL) || (c == KEY_RIGHTCTRL)) kbstate.ctrl = 0;
    if((c == KEY_LEFTSHIFT) || (c == KEY_RIGHTSHIFT)) kbstate.shift = 0;
    if((c == KEY_LEFTALT) || (c == KEY_RIGHTALT)) kbstate.alt = 0;
    return;
}
